// src/Pages/CheckoutPage.js
import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { TextField, Button, Alert } from "@mui/material";
import "../Styles/CheckoutPage.css";
import CheckoutTile from "../Components/CheckoutTile";

function CheckoutPage() {
  const { state } = useLocation();
  const cart = state.data;
  const total = state.total;
  const stripe = useStripe();
  const elements = useElements();

  // Billing form state (you can expand as needed)
  const [billingInfo, setBillingInfo] = useState({
    fullName: "",
    streetAddress: "",
    apartment: "",
    city: "",
    zipCode: "",
    phone: "",
    email: "",
  });
  const [paymentError, setPaymentError] = useState("");
  const [paymentSuccess, setPaymentSuccess] = useState("");
  const [processing, setProcessing] = useState(false);

  // Handle changes in billing fields if needed (omitted for brevity)

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) {
      return;
    }

    setProcessing(true);
    setPaymentError("");

    // Call your backend to create a PaymentIntent or Checkout session.
    // For PaymentIntents, you might have an endpoint like `/api/create-payment-intent`
    // Here we assume your backend returns a clientSecret for PaymentIntent
    try {
      const response = await fetch("http://localhost:5004/api/checkout-session", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          // Include auth header if necessary:
          Authorization: "Bearer " + localStorage.getItem("jwtToken"),
        },
        body: JSON.stringify({
          title: "Checkout Order", // or derive from cart items as needed
          price: total, // total in dollars
        }),
      });
      const { clientSecret, url } = await response.json();
      if (url) {
        // If your backend creates a checkout session instead, redirect to Stripe's hosted checkout:
        window.location.href = url;
        return;
      }

      // If using PaymentIntents, confirm card payment using Stripe Elements
      const cardElement = elements.getElement(CardElement);
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
          billing_details: {
            name: billingInfo.fullName,
            email: billingInfo.email,
            address: {
              line1: billingInfo.streetAddress,
              // Add more address fields as needed
            },
          },
        },
      });

      if (error) {
        setPaymentError(error.message);
      } else if (paymentIntent.status === "succeeded") {
        setPaymentSuccess("Payment successful!");
      }
    } catch (err) {
      setPaymentError("Payment failed: " + err.message);
    }
    setProcessing(false);
  };

  return (
    <div className="CheckoutPage-content">
      <form className="CheckoutPage-holder" onSubmit={handleSubmit}>
        <div className="CheckoutPage-Billing">
          <h1>Billing Information</h1>
          <TextField
            style={{ marginBottom: "5%" }}
            label="Full Name"
            variant="outlined"
            sx={{ input: { color: "#685BE0" } }}
            className="account-input"
            onChange={(e) => setBillingInfo({ ...billingInfo, fullName: e.target.value })}
          />
          <TextField
            style={{ marginBottom: "5%" }}
            label="Street Address"
            variant="outlined"
            sx={{ input: { color: "#685BE0" } }}
            className="account-input"
          />
          {/* Other billing fields as before */}
          <TextField
            style={{ marginBottom: "5%" }}
            label="Email"
            variant="outlined"
            sx={{ input: { color: "#685BE0" } }}
            className="account-input"
            onChange={(e) => setBillingInfo({ ...billingInfo, email: e.target.value })}
          />
        </div>
        <div className="CheckoutPage-Confirm">
          <div className="CheckoutPage-items">
            {cart.map((item) => (
              <CheckoutTile key={item._id} data={item} />
            ))}
          </div>
          <div>
            <h1>Credit Card Information</h1>
          </div>
          <div className="CheckoutPage-payment">
            {/* Replace manual credit card inputs with Stripe CardElement */}
            <CardElement options={{ style: { base: { fontSize: "16px" } } }} />
          </div>
          <div style={{ textAlign: "left" }}>
            <hr />
            <h1>Total: ${total}</h1>
            <div style={{ textAlign: "center" }}>
              <Button 
                type="submit"
                style={{ backgroundColor: "#685BE0" }} 
                variant="contained"
                disabled={processing || !stripe}
              >
                {processing ? "Processing..." : "Process Order"}
              </Button>
            </div>
            {paymentError && <Alert severity="error" style={{ marginTop: "10px" }}>{paymentError}</Alert>}
            {paymentSuccess && <Alert severity="success" style={{ marginTop: "10px" }}>{paymentSuccess}</Alert>}
          </div>
        </div>
      </form>
    </div>
  );
}

export default CheckoutPage;
