import { Button } from "@mui/material";
import "../Styles/DetailsPage.css";
import { useNavigate } from "react-router-dom";
import Alert from '@mui/material/Alert';
import CheckIcon from '@mui/icons-material/Check';
import { useState, useContext } from "react";
import { useLocation } from "react-router-dom";
import Avatar from '@mui/material/Avatar';
import { EditWatchlist, Watchlist, EditWatchBadge } from "../App.js";

function DetailsPage() {
  const navigate = useNavigate();
  const watch = useContext(Watchlist);
  const setWatch = useContext(EditWatchlist);
  const editBadge = useContext(EditWatchBadge);

  // Get the item data passed in via state from the listings page
  const { state } = useLocation();
  const item = state.data;

  // Local state for UI feedback (e.g., watchlist added)
  const [added, setAdded] = useState(false);

  // Add item to watchlist
  function handleAdded() {
    setAdded(true);
    setWatch([...watch, item]);
    editBadge(watch.length + 1);
  }

  // Handle purchase (placeholder for now)
  function handlePurchase() {
    console.log("Send to Stripe API or purchase endpoint");
  }

  // Utility to generate a background color based on a string (for Avatar)
  function stringToHslColor(str, saturation, lightness) {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
      hash = str.charCodeAt(i) + ((hash << 5) - hash);
    }
    hash = hash % 360;
    return `hsl(${hash}, ${saturation}%, ${lightness}%)`;
  }

  // If seller is populated (object) then use seller.username, otherwise use the seller field directly
  const sellerName = item.seller && item.seller.username ? item.seller.username : item.seller;

  return (
    <div className="DetailsPage-container">
      <div className="DetailsPage-holder">
        <div className="DetailsPage-Img-holder">
          {/* Use imageUrl instead of img */}
          <img className="DetailsPage-Img" src={item.imageUrl} alt={item.title} />
        </div>
        <div className="DetailsPage-description">
          <h1>{item.title}</h1>
          <hr />
          <h4>Key Details</h4>
          <ul>
            <li>Condition: {item.condition}</li>
            <li>Delivery: {item.delivery}</li>
            <li>Category: {item.category}</li>
          </ul>
          <h2>Description</h2>
          <p>{item.description}</p>
        </div>
        <div className="DetailsPage-order">
          <Avatar style={{ backgroundColor: stringToHslColor(sellerName || "", 40, 60) }}>
            {sellerName ? sellerName.substring(0, 1).toUpperCase() : "?"}
          </Avatar>
          <p>Sold by {sellerName}</p>
          <h2>${item.price}</h2>
          <p>Shipping: $5 international or Free local</p>
          <Button onClick={handlePurchase} style={{ backgroundColor: "#685BE0", margin: "5%" }} variant="contained">
            Purchase
          </Button>
          {added ? (
            <Alert style={{ marginBottom: "5%" }} icon={<CheckIcon fontSize="inherit" />} severity="success">
              Added to Your Watchlist
            </Alert>
          ) : (
            <div style={{ display: "flex", flexDirection: "column" }}>
              <Button onClick={handleAdded} style={{ backgroundColor: "#685BE0", margin: "5%" }} variant="contained">
                Add to Watchlist
              </Button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default DetailsPage;
