import React, { useState } from "react";
import { Button, TextField } from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../Styles/LoginPage.css";

function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // Send a POST request to your user-service login endpoint
      const response = await axios.post("http://localhost:5002/api/users/login", {
        email,
        password,
      });
      // Save the token (and any other user data if needed) to local storage
      localStorage.setItem("jwtToken", response.data.token);
      // Navigate to the account/dashboard page after successful login
      navigate("/account");
    } catch (error) {
      console.error("Login failed:", error.response ? error.response.data : error.message);
      // Optionally, display an error message to the user here
    }
  };

  return ( 
    <div className="LoginPage-content">
      <div className="LoginPage-holder">
        <div style={{ textAlign: "left" }}>
          <h1>Login</h1>
        </div>
        <div className="LoginPage-inputs">
          <TextField 
            label="Email" 
            variant="outlined" 
            sx={{ input: { color: "#685BE0" } }}
            className="account-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField 
            label="Password" 
            type="password"
            variant="outlined" 
            sx={{ input: { color: "#685BE0" } }}
            className="account-input"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div style={{ textAlign: "center" }}>
          <Button 
            onClick={handleLogin}
            style={{ backgroundColor: "#685BE0", width: "50%" }}
            variant="contained"
          >
            Login
          </Button>
        </div>
        <div>
          <p>
            Don't have an account? <a href="/create-account">Create one here!</a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
