import React, { useState } from "react";
import "../Styles/CreateAccountPage.css";
import { Button, TextField } from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function CreateAccountPage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleCreateAccount = async () => {
    try {
      const response = await axios.post("http://localhost:5002/api/users/register", {
        username,  // now sending username
        email,
        password
      });
      localStorage.setItem("jwtToken", response.data.token);
      navigate("/account");
    } catch (error) {
      console.error("Account creation failed:", error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="CreateAccountPage-content">
      <div className="CreateAccountPage-holder">
        <div style={{ textAlign: "left" }}>
          <h1>Create Account</h1>
        </div>
        <div className="CreateAccountPage-inputs">
          <TextField
            label="Username"
            variant="outlined"
            sx={{ input: { color: "#685BE0" } }}
            className="account-input"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <TextField
            label="Email"
            variant="outlined"
            sx={{ input: { color: "#685BE0" } }}
            className="account-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            label="Password"
            type="password"
            variant="outlined"
            sx={{ input: { color: "#685BE0" } }}
            className="account-input"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div style={{ textAlign: "center" }}>
          <Button
            onClick={handleCreateAccount}
            style={{ backgroundColor: "#685BE0", width: "50%" }}
            variant="contained"
          >
            Create Account
          </Button>
        </div>
      </div>
    </div>
  );
}

export default CreateAccountPage;
